import React , {useState} from 'react';

function App() {

  const [ number , setNumber ] = useState(10)
  const [ secNumber , setSecNumber ] = useState(20)
  const [ result , setResult ] = useState(30);

  /*}
  const [ state , setState ] = useState({
    number : 0,
    secNumber : 0,
    result : 0
  })
  */

  return (
    <div>
      Number 1: <br />
      <input type="text" value={number}/><br />
      Number 2: <br />
      <input type="text" value={secNumber}/><br />
      Result: <br />
      <input type="text" value={result}/><br />
    </div>
  );
}

export default App;
