import React from 'react'
import 'bootswatch/dist/cerulean/bootstrap.min.css'
import {Link} from 'react-router-dom'
import ProductService from '../app/productService'
import ProductRegister from '../views/products/register'


const initialState = {
    name : '',
    sku : '',
    description : '',
    price : '0',
    suplier : '',
    success : false,
    errors: []
}
class Navbar extends React.Component{

    state = initialState;


    changeNavbar = event => {
        if (document.querySelector('#navbarColor01').classList.contains('collapse')) {
            document.querySelector('#navbarColor01').classList.remove('collapse')
        }else{
            document.querySelector('#navbarColor01').classList.add('collapse')
        }
    }

    cleanFields = () =>{

        this.setState(initialState)
        //if(window.location.href.includes("regist-products")){
          //  document.querySelector("#clean").click()
        //}
    }

    render(){
    return(
        <nav className="navbar navbar-expand-lg navbar-dark bg-primary">
            <Link className="navbar-brand" to="/">Products</Link>
            <button onClick={this.changeNavbar} className="navbar-toggler" 
                    type="button" 
                    data-toggle="collapse" 
                    data-target="#navbarColor01" 
                    aria-controls="navbarColor01" 
                    aria-expanded="false" 
                    aria-label="Toggle navigation">     
                <span className="navbar-toggler-icon"></span>
            </button>


        <div className="collapse navbar-collapse" id="navbarColor01">
            <ul className="navbar-nav mr-auto">
                <li className="nav-item active">
                    <Link className="nav-link" to="/" >Home <span className="sr-only">(current)</span></Link>
                </li>
                <li className="nav-item">
                    <Link onClick={this.cleanFields} className="nav-link" to="/regist-products" >Register</Link>
                </li>
                <li className="nav-item">
                    <Link className="nav-link" to="/list-products" >Listing</Link>
                </li>
            </ul>
        </div>
        </nav>
    )
}
}

export default Navbar;
